// SPDX-License-Identifier: MIT
pragma solidity ^0.8.17;

import "@openzeppelin/contracts-4.4.0/token/ERC20/IERC20.sol";

// POC of a reward scheduler contract that can schedule N weeks worth of rewards
contract RewardScheduler {
    address public rewardToken;
    uint256 public lastTimestamp;
    uint256 public numOfWeeksLeft;
    uint256 public amountPerWeek;

    address public immutable rewardForwarder;

    event RewardScheduled(
        address indexed rewardToken,
        uint256 amountPerWeek,
        uint256 numOfWeeks
    );

    constructor(address _rewardForwader) {
        rewardForwarder = _rewardForwader;
    }

    function scheduleReward(
        address _rewardToken,
        uint256 _amount,
        uint256 numberOfWeeks
    ) external {
        require(
            numOfWeeksLeft == 0,
            "RewardScheduler: reward already scheduled"
        );
        rewardToken = _rewardToken;
        lastTimestamp = block.timestamp;
        numOfWeeksLeft = numberOfWeeks;
        uint256 _amountPerWeek = _amount / numberOfWeeks;
        require(_amountPerWeek > 0, "RewardScheduler: amount too small");
        amountPerWeek = _amountPerWeek;

        IERC20(_rewardToken).transferFrom(msg.sender, address(this), _amount);

        emit RewardScheduled(_rewardToken, _amountPerWeek, numberOfWeeks);

        _transferReward();
    }

    function _transferReward() internal {
        numOfWeeksLeft -= 1;
        lastTimestamp = block.timestamp;

        IERC20(rewardToken).transfer(rewardForwarder, amountPerWeek);
    }

    function transferReward() external {
        require(numOfWeeksLeft > 0, "RewardScheduler: no reward scheduled");
        require(
            block.timestamp - lastTimestamp >= 604800,
            "RewardScheduler: not enough time has passed"
        );

        _transferReward();
    }
}

contracts/tokenomics/voting_escrow/VeSDL.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts-4.4.0/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts-4.4.0/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts-4.4.0/utils/math/Math.sol";
import "../../interfaces/IVotingEscrow.sol";

/** @title VeSDLRewards
    @notice Gauge like contract that simulates veSDL stake.
 */

contract VeSDLRewards {
    using SafeERC20 for IERC20;

    IERC20 public rewardToken; // immutable are breaking coverage software should be added back after.
    IVotingEscrow public veToken; // immutable
    uint256 public constant DURATION = 7 days;
    uint256 public periodFinish = 0;
    uint256 public rewardRate = 0;
    uint256 public lastUpdateTime;
    uint256 public rewardPerTokenStored;
    uint256 public queuedRewards = 0;
    uint256 public currentRewards = 0;
    uint256 public historicalRewards = 0;
    address public gov;
    mapping(address => uint256) public userRewardPerTokenPaid;
    mapping(address => uint256) public rewards;
    // whitelisted addresses have right to claim and lock into veSDL on anothers behalf
    mapping(address => bool) public whitelist;

    event RewardAdded(uint256 reward);
    event Donate(uint256 amount);
    event RewardPaid(address indexed user, uint256 reward);
    event UpdatedGov(address gov);
    event UpdatedWhitelist(address recipient, bool isWhitelisted);

    constructor(
        address veToken_,
        address rewardToken_,
        address gov_
    ) {
        veToken = IVotingEscrow(veToken_);
        rewardToken = IERC20(rewardToken_);
        gov = gov_;
    }

    modifier _updateReward(address account) {
        rewardPerTokenStored = rewardPerToken();
        lastUpdateTime = lastTimeRewardApplicable();

        if (account != address(0)) {
            rewards[account] = _earnedReward(account);
            userRewardPerTokenPaid[account] = rewardPerTokenStored;
        }
        _;
    }

    /**
     *  @return timestamp until rewards are distributed
     */
    function lastTimeRewardApplicable() public view returns (uint256) {
        return Math.min(block.timestamp, periodFinish);
    }

    /** @notice reward per token deposited
     *  @dev gives the total amount of rewards distributed since inception of the pool per vault token
     *  @return rewardPerToken
     */
    function rewardPerToken() public view returns (uint256) {
        uint256 supply = veToken.totalSupply();
        if (supply == 0) {
            return rewardPerTokenStored;
        }
        return
            rewardPerTokenStored +
            (((lastTimeRewardApplicable() - lastUpdateTime) *
                rewardRate *
                1e18) / supply);
    }

    function _earnedReward(address account) internal view returns (uint256) {
        return
            (veToken.balanceOf(account) *
                (rewardPerToken() - userRewardPerTokenPaid[account])) /
            1e18 +
            rewards[account];
    }

    /** @notice earning for an account
     *  @return amount of tokens earned
     */
    function earned(address account) external view returns (uint256) {
        return _earnedReward(account);
    }

    /** @notice use to update rewards on veSDL balance changes.
        @dev called by veSDL
     *  @return true
     */
    function updateReward(address _account)
        external
        _updateReward(_account)
        returns (bool)
    {
        require(msg.sender == address(veToken), "!authorized");

        return true;
    }

    /**
     * @notice
     *  Get rewards for an account
     * @dev rewards are transfer to _account
     * @param _account to claim rewards for
     * @param _lock should it lock rewards into veSDL
     * @return true
     */
    function getRewardFor(address _account, bool _lock)
        external
        returns (bool)
    {
        _getReward(
            _account,
            (whitelist[msg.sender] || msg.sender == _account) ? _lock : false
        );
        return true;
    }

    /**
     * @notice
     *  Get rewards
     * @param _lock should it lock rewards into veSDL
     * @return true
     */
    function getReward(bool _lock) external returns (bool) {
        _getReward(msg.sender, _lock);
        return true;
    }

    /**
     * @notice
     *  Get rewards
     * @return true
     */
    function getReward() external returns (bool) {
        _getReward(msg.sender, false);
        return true;
    }

    function _getReward(address _account, bool _lock)
        internal
        _updateReward(_account)
    {
        uint256 reward = rewards[_account];
        if (reward == 0) return;
        rewards[_account] = 0;

        if (_lock) {
            SafeERC20.safeApprove(rewardToken, address(veToken), reward);
            veToken.deposit_for(_account, reward);
        } else {
            SafeERC20.safeTransfer(rewardToken, _account, reward);
        }

        emit RewardPaid(_account, reward);
    }

    /**
     * @notice
     *  Donate tokens to distribute as rewards
     * @dev Do not trigger rewardRate recalculation
     * @param _amount token to donate
     * @return true
     */
    function donate(uint256 _amount) external returns (bool) {
        require(_amount != 0, "==0");
        IERC20(rewardToken).safeTransferFrom(
            msg.sender,
            address(this),
            _amount
        );
        queuedRewards = queuedRewards + _amount;
        emit Donate(_amount);
        return true;
    }

    /**
     * @notice
     * Add new rewards to be distributed over a week
     * @dev Trigger rewardRate recalculation using _amount and queuedRewards
     * @param _amount token to add to rewards
     * @return true
     */
    function queueNewRewards(uint256 _amount) external returns (bool) {
        require(_amount != 0, "==0");
        IERC20(rewardToken).safeTransferFrom(
            msg.sender,
            address(this),
            _amount
        );

        _amount = _amount + queuedRewards;
        _notifyRewardAmount(_amount);
        queuedRewards = 0;

        return true;
    }

    function _notifyRewardAmount(uint256 reward)
        internal
        _updateReward(address(0))
    {
        historicalRewards = historicalRewards + reward;
        if (block.timestamp >= periodFinish) {
            rewardRate = reward / DURATION;
        } else {
            uint256 remaining = periodFinish - block.timestamp;
            uint256 leftover = remaining * rewardRate;
            reward = reward + leftover;
            rewardRate = reward / DURATION;
        }
        currentRewards = reward;
        lastUpdateTime = block.timestamp;
        periodFinish = block.timestamp + DURATION;
        emit RewardAdded(reward);
    }

    /**
     * @notice
     * set gov
     * @dev Can be called by gov
     * @param _gov new gov
     * @return true
     */
    function setGov(address _gov) external returns (bool) {
        require(msg.sender == gov, "!authorized");

        require(_gov != address(0), "0 address");
        gov = _gov;
        emit UpdatedGov(_gov);
        return true;
    }

    /**
     * @notice
     * add to whitelist
     * @dev Can be called by gov
     * @param _addr  address to whitelist
     * @param _isWhitelist whether to whitelist or blacklist
     */
    function addToWhitelist(address _addr, bool _isWhitelist) external {
        require(msg.sender == gov, "!authorized");

        require(_addr != address(0), "0 address");
        whitelist[_addr] = _isWhitelist;
        emit UpdatedWhitelist(_addr, _isWhitelist);
    }

    function sweep(address _token) external returns (bool) {
        require(msg.sender == gov, "!authorized");

        SafeERC20.safeTransfer(
            IERC20(_token),
            gov,
            IERC20(_token).balanceOf(address(this))
        );
        return true;
    }
}

contracts/xchainGauges/AnyCallTranslator.content:
// SPDX-License-Identifier: MIT

pragma solidity ^0.8.17;

import "@openzeppelin/contracts-4.7.3/proxy/transparent/TransparentUpgradeableProxy.sol";
import "@openzeppelin/contracts-4.7.3/proxy/transparent/ProxyAdmin.sol";
import "@openzeppelin/contracts-4.7.3/utils/math/Math.sol";
import "@openzeppelin/contracts-4.7.3/token/ERC20/utils/SafeERC20.sol";

import "@openzeppelin/contracts-upgradeable-4.7.3/access/OwnableUpgradeable.sol";

interface ICallProxy {
    function anyCall(
        address _to,
        bytes calldata _data,
        address _fallback,
        uint256 _toChainId,
        uint256 _flags
    ) external payable; // nonpayable

    function withdraw(uint256 amount) external;

    function executor() external view returns (address executor);
}

interface IAnyCallExecutor {
    function context()
        external
        view
        returns (
            address from,
            uint256 fromChainID,
            uint256 nonce
        );
}

// Empty contract to ensure hardhat import of TransparentUpgradeableProxy contract
contract EmptyProxy is TransparentUpgradeableProxy {
    constructor(
        address _logic,
        address admin_,
        bytes memory _data
    ) payable TransparentUpgradeableProxy(_logic, admin_, _data) {}
}

// Empty contract to ensure hardhat import of ProxyAdmin contract
contract EmptyProxyAdmin is ProxyAdmin {

}

/// @title AnyCallTranslator also know as the AnyCallProxy
/// @notice AnyCallTranslator is responsible for translating messages for AnyCallV6
contract AnyCallTranslator is OwnableUpgradeable {
    using SafeERC20 for IERC20;
    // address of anyCallV6 contract
    address public anyCallContract;
    // address of the AnyCallExecutor contract
    address public anyCallExecutor;
    // mapping of address to whether or not they are allowed to call anyCall
    mapping(address => bool) public isKnownCaller;

    /// @notice AnyCallTranslator constructor
    /// @dev Doesn't do anything except to set isInitialized = true via initializer
    constructor() initializer {
        // Do not do anything on logic contract deployment
        // intializer modifier will prevent any future `initialize()` calls
    }

    receive() external payable {
        // fallback payable function
    }

    /// @notice Initialize the AnyCallTranslator contract for proxies
    /// @dev This needs to be called on proxies of this contract
    /// @param _owner address that will be the owner of this contract
    /// @param _anyCallContract address of the anyCallV6 contract
    function initialize(address _owner, address _anyCallContract)
        public
        initializer
    {
        _transferOwnership(_owner);
        anyCallContract = _anyCallContract;
        anyCallExecutor = ICallProxy(_anyCallContract).executor();
    }

    /// @notice Adds addresses to known caller array
    /// @dev Only callable by owner
    /// @param _callers array of addresses that should be added to known callers
    function addKnownCallers(address[] calldata _callers) external onlyOwner {
        for (uint256 i = 0; i < _callers.length; i++) {
            isKnownCaller[_callers[i]] = true;
        }
    }

    /// @notice Removes addresses from known caller array
    /// @dev Only callable by owner
    function removeKnownCallers(address[] calldata _callers)
        external
        onlyOwner
    {
        for (uint256 i = 0; i < _callers.length; i++) {
            isKnownCaller[_callers[i]] = false;
        }
    }

    /// @notice Set the AnyCall contract address
    /// @dev Only callable by owner
    /// @param _anyCallContract address of the AnyCallV6 contract
    function setAnyCall(address _anyCallContract) external onlyOwner {
        anyCallContract = _anyCallContract;
        anyCallExecutor = ICallProxy(_anyCallContract).executor();
    }

    /// @notice withdraw any ETH that was sent to AnyCall contract
    /// @dev Only callable by owner
    /// @param _amount amount of ETH to withdraw
    function withdraw(uint256 _amount) external onlyOwner {
        ICallProxy(anyCallContract).withdraw(_amount);
    }

    /// @notice Rescue any ERC20 tokens that are stuck in this contract
    /// @dev Only callable by owner
    /// @param token address of the ERC20 token to rescue. Use zero address for ETH
    /// @param to address to send the tokens to
    /// @param balance amount of tokens to rescue
    function rescue(
        IERC20 token,
        address to,
        uint256 balance
    ) external onlyOwner {
        if (address(token) == address(0)) {
            // for Ether
            uint256 totalBalance = address(this).balance;
            balance = balance == 0
                ? totalBalance
                : Math.min(totalBalance, balance);
            require(balance > 0, "trying to send 0 ETH");
            // slither-disable-next-line arbitrary-send
            (bool success, ) = to.call{value: balance}("");
            require(success, "ETH transfer failed");
        } else {
            // any other erc20
            uint256 totalBalance = token.balanceOf(address(this));
            balance = balance == 0
                ? totalBalance
                : Math.min(totalBalance, balance);
            require(balance > 0, "trying to send 0 balance");
            token.safeTransfer(to, balance);
        }
    }

    /// @notice Send a cross chain call via anyCallV6
    /// @dev Only callable by known callers. Fee flags should be set to 2 in most
    /// if not all use cases involing this contract.
    /// @param _to address to call
    /// @param _data calldata with function signature to use
    /// @param _fallback address of the fallback contract to use if the call fails
    /// @param _toChainId chainId to send the call to
    /// @param _flags flags for determining on which network should caller pay the fee
    /// (0 = desitnation chain, 2 = origin chain)
    function anyCall(
        address _to,
        bytes calldata _data,
        address _fallback,
        uint256 _toChainId,
        // Use 0 flag to pay fee on destination chain, 1 to pay on source
        uint256 _flags
    ) external payable {
        require(isKnownCaller[msg.sender], "Unknown caller");
        ICallProxy(anyCallContract).anyCall{value: msg.value}(
            address(this),
            abi.encode(_to, _data),
            _fallback,
            _toChainId,
            _flags
        );
    }

    /// @notice Receive a cross chain call via anyCallV6 and execute a call on this network
    /// @dev Only callable anyCallExecutor, the executor contract of anyCallV6
    /// @param toAndData abi encoded target address and function calldata to execute
    /// @return boolean indicating success of the call
    /// @return bytes any data returned from the call
    function anyExecute(bytes calldata toAndData)
        external
        returns (bool, bytes memory)
    {
        // Check that caller is anycall executor
        require(
            msg.sender == anyCallExecutor,
            "Caller is not anyCall executor"
        );
        // Get the address of the caller from the source chain
        (address _from, , ) = IAnyCallExecutor(msg.sender).context();
        // Check that caller is AnyCallTranslator
        require(_from == address(this), "Wrong context");

        // Decode to and data
        (address to, bytes memory data) = abi.decode(
            toAndData,
            (address, bytes)
        );
        (bool success, bytes memory returnData) = to.call(data);
        require(success, "Target call failed");
        return (success, returnData);
    }
}

contracts/xchainGauges/bridgers/Arbitrum.content:
// SPDX-License-Identifier: MIT

pragma solidity ^0.8.17;

import "@openzeppelin/contracts-4.7.3/token/ERC20/utils/SafeERC20.sol";
import "./Bridger.sol";

/// @notice Interface for the official Arbitrum Gateway Router contract
interface IGatewayRouter {
    function getGateWay(address _token) external view returns (address);

    function outboundTransferCustomRefund(
        address _l1Token,
        address _refundTo,
        address _to,
        uint256 _amount,
        uint256 _maxGas,
        uint256 _gasPriceBid,
        bytes calldata _data
    ) external payable;

    function getOutboundCalldata(
        address _token,
        address _from,
        address _to,
        uint256 _amount,
        bytes calldata _data
    ) external view returns (uint256, uint256);
}

/// @notice Interface for the official Arbitrum Inbox contract
interface Inbox {
    function calculateRetryableSubmissionFee(
        uint256 _data_length,
        uint256 _base_fee
    ) external view returns (uint256);
}

/// @title Arbitrum bridger contract
/// @notice This contract is used to bridge tokens to Arbitrum
/// @dev Arbitrum bridge requires base fee for the fee calculation therefore
/// the function may revert on evm implementations that do not support base fee
contract ArbitrumBridger is Bridger {
    using SafeERC20 for IERC20;

    // SDL token address
    address private immutable SDL;
    // Arbitrum: L1 ERC20 Gateway
    address private constant ARB_GATEWAY =
        0xa3A7B6F88361F48403514059F1F16C8E78d60EeC;
    // Arbitrum: L1 Gateway Router
    address private constant ARB_GATEWAY_ROUTER =
        0x72Ce9c846789fdB6fC1f34aC4AD25Dd9ef7031ef;
    // Arbitrum: L1 Inbox
    address private constant INBOX = 0x4Dbd4fc535Ac27206064B68FfCf827b0A60BAB3f;

    // Submission data variable which includes gas limit and gas price
    uint256 private submissionData;
    // Mapping of token addresses to their approval status
    mapping(address => bool) public approved;

    /// @notice Event to emit when gas limit and gas price are updated
    /// @param oldSubmissionData previous submission data
    /// @param newSubmissionData new submission data
    event UpdateSubmissionData(
        uint256[2] oldSubmissionData,
        uint256[2] newSubmissionData
    );

    /// @notice Constructor for this contract
    /// @param _gasLimit The gas limit for the outbound transfer
    /// @param _gasPrice The gas price for the outbound transfer
    /// @param _SDL The address of the SDL token
    constructor(
        uint256 _gasLimit,
        uint256 _gasPrice,
        address _SDL
    ) {
        SDL = _SDL;
        // Construct submission data
        // uint128 gasLimit
        // uint128 gasPrice
        // uint256 submissionData = (gasLimit << 128) + gasPrice
        require(_gasLimit < type(uint128).max && _gasPrice < type(uint128).max);
        submissionData = (_gasLimit << 128) + _gasPrice;
        emit UpdateSubmissionData(
            [uint256(0), uint256(0)],
            [_gasLimit, _gasPrice]
        );

        // Approve SDL to be used by the associated arbitrum gateway contract
        IERC20(_SDL).safeApprove(ARB_GATEWAY, type(uint256).max);
        approved[_SDL] = true;
    }

    /// @notice Bridge tokens from the current chain to Arbitrum
    /// @dev Due to `whenNotPaused` modifier, this function can only be called when
    /// the contract is not paused. The sender must have approved this contract.
    /// @param _token address of the token to bridge
    /// @param _to address of the destination account on Arbitrum
    /// @param _amount amount of tokens to bridge
    function bridge(
        address _token,
        address _to,
        uint256 _amount
    ) external payable override whenNotPaused {
        // Transfer tokens from the caller to this
        IERC20(_token).safeTransferFrom(msg.sender, address(this), _amount);

        // If the token is not SDL, approve the token transfer to appropriate gateway
        if (_token != SDL && !approved[_token]) {
            IERC20(_token).safeApprove(
                IGatewayRouter(ARB_GATEWAY_ROUTER).getGateWay(_token),
                type(uint256).max
            );
            approved[_token] = true;
        }

        // Unpack submission data
        uint256 data = submissionData;
        uint256 _gasLimit = data >> 128;
        uint256 _gasPrice = data & type(uint128).max;

        // Calculate submission cost based on current base fee
        (, uint256 calldataSize) = IGatewayRouter(ARB_GATEWAY_ROUTER)
            .getOutboundCalldata(_token, address(this), _to, _amount, "");
        uint256 submissionCost = Inbox(INBOX).calculateRetryableSubmissionFee(
            calldataSize + 256,
            block.basefee
        );

        // Use unpacked submission data and calculated submission cost to calculate
        // value to send with the outbound transfer
        address _owner = owner();
        IGatewayRouter(ARB_GATEWAY_ROUTER).outboundTransferCustomRefund{
            value: _gasLimit * _gasPrice + submissionCost
        }(
            _token,
            _owner,
            _to,
            _amount,
            _gasLimit,
            _gasPrice,
            abi.encode(submissionCost, "")
        );

        // Send any remaining ETH to the owner
        if (address(this).balance != 0) {
            payable(_owner).transfer(address(this).balance);
        }
    }

    /// @notice Check if this bridger can be used
    /// @return true if this bridger can be used
    function check(address) external pure override returns (bool) {
        return true;
    }

    /// @notice Read the stored gas limit
    /// @return gas limit to use for arbitrum outbound transfers
    function gasLimit() external view returns (uint256) {
        return submissionData >> 128;
    }

    /// @notice Read the stored gas price
    /// @return gas price to use for arbitrum outbound transfers
    function gasPrice() external view returns (uint256) {
        return submissionData & type(uint128).max;
    }

    /// @notice Calculate the total gas cost for an outbound transfer
    /// @return total gas cost for an outbound transfer
    function cost() external view override returns (uint256) {
        return cost(block.basefee);
    }

    /// @notice Calculate the total gas cost for an outbound transfer. Uses the
    /// provided base fee instead of the current base fee.
    /// @param basefee The basefee to use for the calculation
    /// @return total gas cost for an outbound transfer
    function cost(uint256 basefee) public view returns (uint256) {
        // Calculate submission cost based on current base fee
        (, uint256 calldataSize) = IGatewayRouter(ARB_GATEWAY_ROUTER)
            .getOutboundCalldata(SDL, address(this), msg.sender, 10**36, "");
        uint256 submissionCost = Inbox(INBOX).calculateRetryableSubmissionFee(
            calldataSize + 256,
            basefee
        );
        uint256 data = submissionData;
        // gasLimit * gasPrice + maxSubmissionCost
        return ((data >> 128) * (data & type(uint128).max) + submissionCost);
    }

    /// @notice Update the gas limit and gas price for outbound transfers
    /// @dev only callable by the owner
    /// @param _gasLimit gas limit to store for the outbound transfer
    /// @param _gasPrice gas price to store for the outbound transfer
    function setSubmissionData(uint256 _gasLimit, uint256 _gasPrice)
        external
        onlyOwner
    {
        // construct submission data
        require(_gasLimit < type(uint128).max && _gasPrice < type(uint128).max);
        uint256 data = submissionData;
        submissionData = (_gasLimit << 128) + _gasPrice;
        emit UpdateSubmissionData(
            [data >> 128, data & type(uint128).max],
            [_gasLimit, _gasPrice]
        );
    }
}

