// SPDX-License-Identifier: MIT
pragma solidity ^0.8.17;

import "@openzeppelin/contracts-4.7.3/token/ERC20/utils/SafeERC20.sol";

/// @title Child Oracle contract
/// @notice ChildOracle contract is responsible for receiving data from the parent chain
contract ChildOracle {
    // address for call proxy contract that is responsible for calling the oracle
    address public callProxy;
    // address of the owner of the contract
    address public owner;
    // address of the future owner of the contract
    address public futureOwner;

    // mapping of user address to the Point data that is received from the parent chain
    mapping(address => Point) public userPoints;
    // total supply data that is received from the parent chain
    Point public globalPoint;

    /// @notice Point struct is used to store the veSDL data from the parent chain
    /// @param bias Bias of the amount at the time of the update
    /// @param slope Slope of amount per second that should be reduced
    /// @param ts Timestamp of the update
    struct Point {
        int128 bias;
        int128 slope;
        uint256 ts;
    }

    /// @notice Emitted when ownership is transferred
    /// @param oldOwner Previous owner of the contract
    /// @param newOwner New owner of the contract
    event TransferOwnership(address oldOwner, address newOwner);

    /// @notice Emitted when call proxy address is updated
    /// @param oldCallProxy Previous call proxy address
    /// @param newCallProxy New call proxy address
    event UpdateCallProxy(address oldCallProxy, address newCallProxy);

    /// @notice Emitted when this contract receives data from the parent chain
    /// @param userPoint Point data of the user
    /// @param globalPoint Point data of the total supply
    /// @param user User address
    event Receive(Point userPoint, Point globalPoint, address user);

    /// @notice Constructor for ChildOracle contract
    /// @dev Sets the owner as the deployer of the contract
    /// @param _callProxy CallProxy contract address. This should be set to AnyCallTranslator.
    constructor(address _callProxy) {
        callProxy = _callProxy;
        emit UpdateCallProxy(address(0), _callProxy);
        owner = msg.sender;
        emit TransferOwnership(address(0), msg.sender);
    }

    /// @notice Read the stored veSDL balance of the user
    /// @param _user User address
    /// @return veSDL balance of the user
    function balanceOf(address _user) external view returns (uint256) {
        Point memory lastPoint = userPoints[_user];
        lastPoint.bias -=
            lastPoint.slope *
            int128(int256(block.timestamp - lastPoint.ts));
        if (lastPoint.bias < 0) {
            lastPoint.bias = 0;
        }
        return (uint256(uint128(lastPoint.bias)));
    }

    /// @notice Read the stored veSDL total supply
    /// @return veSDL total supply
    function totalSupply() external view returns (uint256) {
        Point memory lastPoint = globalPoint;
        lastPoint.bias -=
            lastPoint.slope *
            int128(int256(block.timestamp - lastPoint.ts));
        if (lastPoint.bias < 0) {
            lastPoint.bias = 0;
        }
        return (uint256(uint128(lastPoint.bias)));
    }

    /// @notice Receive data from the parent chain for a user account
    /// @dev This function can only be called by the callProxy contract.
    /// Until this data is received, the user's veSDL balance will be treated as 0.
    /// @param _userPoint veSDL balance data of the user
    /// @param _globalPoint veSDl total supply data
    /// @param _user User address
    function receive(
        Point memory _userPoint,
        Point memory _globalPoint,
        address _user
    ) external {
        require(msg.sender == callProxy, "not translator");
        Point storage prevUserPoint = userPoints[_user];
        if (_userPoint.ts > prevUserPoint.ts) {
            userPoints[_user] = _userPoint;
        }

        Point memory prevGlobalPoint = globalPoint;
        if (_globalPoint.ts > prevGlobalPoint.ts) {
            globalPoint = _globalPoint;
        }

        emit Receive(_userPoint, _globalPoint, _user);
    }

    /// @notice Set the callProxy contract address
    /// @dev callProxy contract address should be set to AnyCallTranslator.
    /// @param _newCallProxy CallProxy contract address
    function setCallProxy(address _newCallProxy) external {
        require(msg.sender == owner, "not owner");
        emit UpdateCallProxy(callProxy, _newCallProxy);
        callProxy = _newCallProxy;
    }

    /// @notice Commit to transfer ownership of the contract
    /// @param _futureOwner Address of the new owner
    function commitTransferOwnership(address _futureOwner) external {
        require(msg.sender == owner);
        futureOwner = _futureOwner;
    }

    /// @notice Accept the transfer of ownership of the contract
    /// @dev Must be called by the futureOwner
    function acceptTransferOwnership() external {
        require(msg.sender == futureOwner);
        emit TransferOwnership(owner, msg.sender);
        owner = msg.sender;
    }
}

contracts/xchainGauges/oracles/RootOracl.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.17;

import "@openzeppelin/contracts-4.7.3/token/ERC20/utils/SafeERC20.sol";

/// @notice Point struct is used to store the veSDL data from the parent chain
/// @param bias Bias of the amount at the time of the update
/// @param slope Slope of amount per second that should be reduced
/// @param ts Timestamp of the update
struct Point {
    int128 bias;
    int128 slope;
    uint256 ts;
}

// Interface compatible with AnyCall contract
interface ICallProxy {
    function anyCall(
        address _to,
        bytes calldata _data,
        address _fallback,
        uint256 _toChainId,
        uint256 _flags
    ) external;
}

// Inteface compatible with RootGaugeFactory
interface Factory {
    function get_bridger(uint256 _chain_id) external view returns (address);
}

// Inteface compatible with VotingEscrow
interface votingEscrow {
    function epoch() external view returns (uint256);

    function point_history(uint256 _idx) external view returns (Point memory);

    function user_point_epoch(address _user) external view returns (uint256);

    function user_point_history(address _user, uint256 _idx)
        external
        view
        returns (Point memory);
}

/// @title Root Oracle contract
/// @notice RootOracle contract is responsible for reading veSDL data from this chain
/// and sending it to the child chains
contract RootOracle {
    // consts
    // RootGaugeFactory contract address
    address public immutable FACTORY;
    // VotingEscrow contract address
    address public immutable VE;

    // events
    /// @notice Emitted when ownership is transferred
    /// @param oldOwner Previous owner of the contract
    /// @param newOwner New owner of the contract
    event TransferOwnership(address oldOwner, address newOwner);

    /// @notice Emitted when call proxy address is updated
    /// @param oldCallProxy Previous call proxy address
    /// @param newCallProxy New call proxy address
    event UpdateCallProxy(address oldCallProxy, address newCallProxy);

    // address for call proxy contract that is responsible for bridging the data
    address public callProxy;
    // address of the owner of the contract
    address public owner;
    // address of the future owner of the contract
    address public futureOwner;

    /// @notice Constructor for RootOracle contract
    /// @param _factory RootGaugeFactory contract address
    /// @param _ve VotingEscrow contract address
    /// @param _callProxy CallProxy contract address. This should be set to AnyCallTranslator.
    constructor(
        address _factory,
        address _ve,
        address _callProxy
    ) {
        FACTORY = _factory;
        VE = _ve;

        callProxy = _callProxy;
        emit UpdateCallProxy(address(0), _callProxy);

        owner = msg.sender;
        emit TransferOwnership(address(0), msg.sender);
    }

    /// @notice Push the veSDL data of the caller to the child chain
    /// @dev This will be reverted if caller doesn't have any veSDL balance
    /// @param _chainId Chain ID of the child chain to push the data
    function push(uint256 _chainId) external {
        address user = msg.sender;
        require(
            Factory(FACTORY).get_bridger(_chainId) != address(0),
            "Bridger not found"
        );

        require(IERC20(VE).balanceOf(user) != 0, "No ve balance");
        Point memory userPoint = votingEscrow(VE).user_point_history(
            user,
            votingEscrow(VE).user_point_epoch(user)
        );
        Point memory globalPoint = votingEscrow(VE).point_history(
            votingEscrow(VE).epoch()
        );
        ICallProxy(callProxy).anyCall(
            address(this),
            abi.encodeWithSelector(
                bytes4(
                    keccak256(
                        "receive((int128,int128,uint256),(int128,int128,uint256),address)"
                    )
                ),
                userPoint,
                globalPoint,
                user
            ),
            address(0),
            _chainId,
            0
        );
    }

    /// @notice Push the veSDL data of an account to the child chain
    /// @dev This will be reverted if user doesn't have any veSDL balance
    /// @param _chainId Chain ID of the child chain to push the data
    /// @param _user Address of the account to push the data for
    function push(uint256 _chainId, address _user) external {
        require(
            Factory(FACTORY).get_bridger(_chainId) != address(0),
            "Bridger not found"
        );
        require(IERC20(VE).balanceOf(_user) != 0, "No ve balance");
        Point memory userPoint = votingEscrow(VE).user_point_history(
            _user,
            votingEscrow(VE).user_point_epoch(_user)
        );
        Point memory globalPoint = votingEscrow(VE).point_history(
            votingEscrow(VE).epoch()
        );
        ICallProxy(callProxy).anyCall(
            address(this),
            abi.encodeWithSelector(
                bytes4(
                    keccak256(
                        "receive((int128,int128,uint256),(int128,int128,uint256),address)"
                    )
                ),
                userPoint,
                globalPoint,
                _user
            ),
            address(0),
            _chainId,
            0
        );
    }

    /// @notice Set the call proxy address
    /// @dev This can only be called by the owner of the contract
    /// @param _newCallProxy Address of the new call proxy
    function setCallProxy(address _newCallProxy) external {
        require(msg.sender == owner, "not owner");
        emit UpdateCallProxy(callProxy, _newCallProxy);
        callProxy = _newCallProxy;
    }

    /// @notice Commit to transfer of the ownership of the contract
    /// @dev This can only be called by the owner of the contract
    /// @param _futureOwner Address of the new owner
    function commitTransferOwnership(address _futureOwner) external {
        require(msg.sender == owner);
        futureOwner = _futureOwner;
    }

    /// @notice Accept the ownership of the contract
    /// @dev This can only be called by the future owner of the contract
    function acceptTransferOwnership() external {
        require(msg.sender == futureOwner);
        emit TransferOwnership(owner, msg.sender);
        owner = msg.sender;
    }
}

contracts/xchainGauges/RewardForwarder.s.content:
// SPDX-License-Identifier: MIT

pragma solidity ^0.8.17;

import "@openzeppelin/contracts-4.7.3/token/ERC20/utils/SafeERC20.sol";

/// @notice interface compatible with LiquidityGaugeV5 or ChildGauge
interface IGauge {
    function deposit_reward_token(address _reward_token, uint256 amount)
        external; // nonpayable
}

/// @title RewardForwarder contract for gauges
/// @notice RewardForwarder is responsible for forwarding rewards to gauges
/// in permissionlessly manner
contract RewardForwarder {
    using SafeERC20 for IERC20;

    // address of the associated gauge
    address immutable GAUGE;

    /// @notice RewardForwarder constructor. Sets the gauge address.
    /// @param _gauge address of the associated gauge
    constructor(address _gauge) {
        GAUGE = _gauge;
    }

    /// @notice Deposit the reward token in this contract to the gauge
    /// @dev Upon calling this function, the reward token will be
    /// transferred from this contract to the gauge.
    /// @param _rewardToken address of the reward token to deposit
    function depositRewardToken(address _rewardToken) external {
        IGauge(GAUGE).deposit_reward_token(
            _rewardToken,
            IERC20(_rewardToken).balanceOf(address(this))
        );
    }

    /// @notice Allow the gauge to use the reward token in this contract
    /// @dev This must be called before `depositRewardToken` can be called successfully
    /// @param _rewardToken address of the reward token to deposit
    function allow(address _rewardToken) external {
        IERC20(_rewardToken).safeApprove(GAUGE, 0);
        IERC20(_rewardToken).safeApprove(GAUGE, type(uint256).max);
    }

    /// @notice Read the associated gauge address
    /// @return gauge address
    function gauge() external view returns (address) {
        return (GAUGE);
    }
}

